import React, { useState } from "react";

import { fetchCards } from "../api";

const SearchBar = ({ setResults }) => {
  console.log("rendering SearchBar");
  const [name, setName] = useState("");
  const [text, setText] = useState("");

  const cardNameChange = (event) => {
    setName(event.target.value);
  };
  const cardTextChange = (event) => {
    setText(event.target.value);
  };

  async function searchCards(event) {
    console.log("CLICKED ON SEARCH");
    // prevent Default
    event.preventDefault();
    // grab ahold of text inside of input (What is my user searching for?)
    // call my api (fetchCards) and pass in the user form input
    const newResults = await fetchCards([name, text]);
    // call setResults so that other components know about this change of state
    setResults(newResults);
  }

  return (
    <div id="search">
      <h3>Look up cards here...</h3>
      <form onSubmit={searchCards}>
        <input
          type="text"
          placeholder="card name"
          value={name}
          onChange={cardNameChange}
        />
        <input
          type="text"
          placeholder="card text"
          vaule={text}
          onChange={cardTextChange}
        />
        <button type="submit">Search</button>
      </form>
    </div>
  );
};

export default SearchBar;
